import React, { useEffect } from 'react';
import './App.css';
import reactLogo from './img/react-logo.svg';
import img1 from './img/H2L3ohY.png';
import img2 from './img/IHW1mFB.png';
import img3 from './img/LtUDB9y.png';
import img4 from './img/agN6R4Y.png';

const drawReactLogo = (context, width, height, x, y) => {
  const image = new Image();
  image.src = "./img/react-logo.svg"
  image.onload = () => {
    context.drawImage(image, x, y, width, height);    
  };
};

function App() {
  useEffect(() => {
       const canvas = document.getElementById('logo-canvas');
       const context = canvas.getContext('2d');

       drawReactLogo(context, 170, 60, 0, -10);
       drawReactLogo(context, 140, 45, 75, 53);
       drawReactLogo(context, 155, 50, 0, 100);
       drawReactLogo(context, 80, 30, 250, 20);
       drawReactLogo(context, 80, 30, 250, 60);
       drawReactLogo(context, 80, 30, 250, 100);

  }, []);  

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <h1>Say hello to <br/>ReactJS</h1>
          <p>You will learn Frontend<br/>framework from scratch, to <br/>become a Ninja Developer.</p>
          <button className="btn">Awesome!</button>          
        </div>
        <div className="canvas-container">
          <canvas id="logo-canvas" className="app-logo" />          
        </div>
        </header>
        <section className="footer">
          <div className="feature">
            <img src="../img/H2L3ohY.png" alt="" />
            <h1>Declarative</h1>
            <p>React makes it <br/> painless to create <br/> interactive UIs.</p>            
          </div>
          <div className="feature">
            <img src="../img/IHW1mFB.png" alt="" />
            <h1>Components</h1>
            <p>Build encapsulated <br/> components that <br/> manage their state.</p>
          </div>
          <div className="feature">
          <img src="../img/LtUDB9y.png" alt="" />
          <h1>Single-Way</h1>
          <p>A set of immutable <br/> values is passed to <br/> the component's.</p>
          </div>
          <div className="feature">
          <img src="../img/agN6R4Y.png" alt="" />
          <h1>JSX</h1>
          <p>Statically-typed,<br/> designed to run on <br/> modern browsers.</p>
          </div>
          </section>
          </div>             
  );
}

export default App;
